import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';


const Container = styled.div`
  background-image: url("https://images.unsplash.com/photo-1687201363580-1cd1bb33c2b5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1332&q=80");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center top;
  overflow-x: hidden;
  height: 100vh;
  position: relative;
`;

const Header = styled.header`
  position: absolute;
  top: 0;
  right: 0;
  padding-right: 20px;
  z-index: 1;
`;

const HeaderItem = styled.a`
  display: inline-block;
  font-size: 1.2rem;
  padding: 10px;
  color: white;

  &:hover {
    color: #ffffff80; /* Semi-transparent white on hover */
  }
`;

const BirdsContainer = styled.div`
  position: absolute;
   top: 10vw;
  animation: birdsFlying 30s linear infinite;
  animation-delay: 0s;
  animation-timing-function: linear;
  //z-index: -1;

  @keyframes birdsFlying {
    0% {
      left: 100vw;
    }
    100% {
      left: -200vw;
    }
  }
`;

const WelcomeText = styled.div`
  color: white;
  text-shadow: 2px 2px 5px black;
  position: fixed;
  text-align: center;
  height: 100vh;
  line-height: 100vh;
  font-size: 40px;
  font-family: "Courier New", Courier, monospace;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 0;
`;

const Welcome = () => {
    return (
      <Container>
        {/* ... Other elements ... */}
        <BirdsContainer>
          <img src="https://clipart-library.com/img1/1663317.gif" width='200px' height="10px" draggable="false" alt="Birds" />
        </BirdsContainer>
        
      <div
          className='flex flex-col items-center justify-center
        mt-4 p-4'
        >
          <h1 className='text-3xl text-white text-center leading-80 font-semibold'>
          
        Welcome to SwiftSynopsis! Our app allows you to effortlessly summarize your text using the power of OpenAI Summarizer. Just paste your content, select a language, and get a concise summary in no time.
      
            <br />
           
          </h1>
         
        </div>
        {/* Use the Link component to navigate to App.js */}
        <Link to="/app">
          <WelcomeText>Explore â†’</WelcomeText>
        </Link>
      </Container>
    );
  };
  
export default Welcome;
