import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ScrollReveal from 'scrollreveal';

const Welcome = () => {
  const navigate = useNavigate();

  const onStart = () => {
    navigate('/app');
  };

  useEffect(() => {
    // JavaScript styling
    const doc = document;
    const rootEl = doc.documentElement;
    const body = doc.body;
    const lightSwitch = doc.getElementById('lights-toggle');
    const sr = window.sr = ScrollReveal();

    rootEl.classList.remove('no-js');
    rootEl.classList.add('js');

    window.addEventListener('load', function () {
      body.classList.add('is-loaded');
    });

    function revealAnimations() {
      sr.reveal('.feature', {
        duration: 600,
        distance: '20px',
        easing: 'cubic-bezier(0.215, 0.61, 0.355, 1)',
        origin: 'right',
        viewFactor: 0.2
      });
    }

    if (body.classList.contains('has-animations')) {
      window.addEventListener('load', revealAnimations);
    }

    if (lightSwitch) {
      window.addEventListener('load', checkLights);
      lightSwitch.addEventListener('change', checkLights);
    }

    function checkLights() {
      let labelText = lightSwitch.parentNode.querySelector('.label-text');
      if (lightSwitch.checked) {
        body.classList.remove('lights-off');
        if (labelText) {
          labelText.innerHTML = 'dark';
        }
      } else {
        body.classList.add('lights-off');
        if (labelText) {
          labelText.innerHTML = 'light';
        }
      }
    }
  }, []);

  return (
    <div className="welcome-page">
      <h1>Welcome to SwiftSynopsis!</h1>
      <p>Get quick summaries powered by OpenAI Summarizer.</p>
      <button onClick={onStart}>Start</button>
    </div>
  );
};

export default Welcome;
