import { useEffect, useState } from "react";
import Delete from "./assets/delete.png";
import styled from "styled-components";

const Background = styled.div`
  background-image: url("https://images.unsplash.com/photo-1687201363580-1cd1bb33c2b5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1332&q=80");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center top;
  opacity: 0.5; /* Adjust the opacity as needed */
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
`;

const languageOptions = [
  { value: "en", label: "English" },
  { value: "sv", label: "Swedish" },
  { value: "de", label: "German" },
  { value: "es", label: "Spanish" },
  { value: "ar", label: "Arabic" },
];

function App() {
  const [value, setValue] = useState("");
  const [data, setData] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [isCopy, setIsCopy] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState("en");

  const handleLanguageChange = (event) => {
    setSelectedLanguage(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const response = await fetch("https://api.openai.com/v1/engines/text-davinci-003/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer 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",
        },
        body: JSON.stringify({
          prompt: `${value}\n\nTl;dr`,
          temperature: 0.1,
          max_tokens: Math.floor(value.length / 2),
          top_p: 1,
          frequency_penalty: 0,
          presence_penalty: 0.5,
          stop: ['"""'],
        }),
      });

      if (!response.ok) {
        throw new Error("Request failed");
      }

      const data = await response.json();
      const translatedText = data.choices[0].text;

      setData((prevData) => [translatedText, ...prevData]);
    } catch (error) {
      console.error("Error:", error);
    } finally {
      setSubmitting(false);
    }
  };

  const handleCopy = async (txt) => {
    try {
      await navigator.clipboard.writeText(txt);
      setIsCopy(true);
      setTimeout(() => setIsCopy(false), 1500);
    } catch (err) {
      console.log(err);
    }
  };

  const handleDelete = (txt) => {
    const filtered = data.filter((d) => d !== txt);
    setData(filtered);
    localStorage.setItem("summary", JSON.stringify(filtered));
  };

  useEffect(() => {
    const result = localStorage.getItem("summary");
    setData(result ? JSON.parse(result).reverse() : []);
  }, []);

  return (
    <div className="w-full bg-[#0f172a] min-h-screen p-4 md:p-20">
      <Background />
      <div className="w-full">
        <div className="flex justify-between items-center w-full h-10 px-5 2xl:px-40">
          <h3 className="cursor-pointer text-3xl font-bold text-cyan-600">SwiftSynopsis!</h3>
        </div>

        <div className="flex flex-col items-center justify-center mt-4 p-4">
          <h1 className="text-3xl text-white text-center leading-10 font-semibold">
            Summarizer with<br />
            <span className="text-5xl font-bold text-cyan-500">OpenAI</span>
          </h1>
          <p className="mt-5 text-lg text-gray-500 sm:text-xl text-center max-w-2xl">
            Effortlessly submit your text and receive a concise synopsis powered by OpenAI Summarizer
          </p>
        </div>

        <div className="flex flex-col w-full items-center justify-center mt-5">
          <textarea
            placeholder="Paste your content here ..."
            rows={16}
            className="block w-full md:w-[650px] rounded-md border border-slate-700 bg-slate-800 p-2 text-sm shadow-lg font-medium text-white focus:border-gray-500 focus:outline-none focus:ring-0"
            value={value}
            onChange={(e) => setValue(e.target.value)}
          ></textarea>

          {value.length > 0 && (
            <div className="mt-5">
              <select
                value={selectedLanguage}
                onChange={handleLanguageChange}
                className="bg-slate-800 text-white rounded-md px-4 py-3"
                style={{ width: "120px" }}
              >
                {languageOptions.map((option) => (
                  <option key={option.value} value={option.value} style={{ color: "white", backgroundColor: "transparent" }}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          )}

          {value.length > 0 && (
            <button
              className="mt-5 bg-blue-500 px-5 py-2 text-white text-md cursor-pointer rounded-md"
              onClick={handleSubmit}
              disabled={submitting}
            >
              {submitting ? "Please wait..." : "Submit"}
            </button>
          )}
        </div>
      </div>

      <div className="w-full mt-10 flex flex-col gap-5 shadow-md items-center justify-center">
        {data.length > 0 && (
          <>
            <p className="text-white font-semibold text-lg">Summary History</p>
            {data.map((d, index) => (
              <div key={index} className="max-w-2xl bg-slate-800 p-3 rounded-md">
                <p className="text-gray-400 text-lg">{d}</p>
                <div className="flex gap-5 items-center justify-end mt-2">
                  <p className="text-gray-500 font-semibold cursor-pointer" onClick={() => handleCopy(d)}>
                    {isCopy ? "Copied" : "Copy"}
                  </p>
                  <span className="cursor-pointer" onClick={() => handleDelete(d)}>
                    <img src={Delete} alt="Delete" className="w-6 h-6" />
                  </span>
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
}

export default App;
